<script setup lang="ts">
  const layer = ref<HTMLElement | null>(null);
  const points = ref<HTMLElement[]>([]);

  function setPoint(el: any) {
    points.value.push(el);
  }

  useIntersect(
    layer,
    () => {
      points.value.forEach((point, index) => {
        setTimeout(() => {
          point.classList.add('point--fade');
        }, 200 + 30 * index);
      });
    },
    () => {},
    { threshold: 0.5 },
  )
</script>

<style scoped lang="scss">
  .layer {
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    z-index: 10;
  }

  .point {
    visibility: hidden;

    &--fade {
      visibility: visible;
      animation-name: fadeInDown;
      animation-duration: 1s;
    }
  }
</style>
